import React, { Component } from 'react';
import index from '../../config/index';

class Login extends Component {
    constructor(props) {
        super(props);
        this.login = this.login.bind(this);
        this.register = this.register.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            email: '',
            password: ''
        }
    }

    login(e) {
        e.preventDefault();
        fire.auth().signInWithEmailAndPassword(this.state.email, this.state.password).catch((error) => {
        });
    }

    register(e) {
        e.preventDefault();
        fire.auth().createUserWithEmailAndPassword(this.state.email, this.state.password).catch((error) => {
        });
    }

    handleChange(e) {
        this.setState({ [e.target.name]: e.target.value });
    }

    render() {
        return (
            <div class="login-form">
                <form>
                    <h2 class="text-center">Sign in</h2>		
                    <div class="text-center social-btn">
                        <a href="/" class="btn btn-primary btn-block"><i class="fa fa-facebook"></i> Sign in with <b>Facebook</b></a>
                        <a href="/" class="btn btn-info btn-block"><i class="fa fa-twitter"></i> Sign in with <b>Twitter</b></a>
                        <a href="/" class="btn btn-danger btn-block"><i class="fa fa-google"></i> Sign in with <b>Google</b></a>
                    </div>
                    <div class="or-seperator"><i>or</i></div>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-envelope"></i></span>
                            <input 
                                type="email"
                                name="email" 
                                value={this.state.email} 
                                onChange={this.handleChange} 
                                class="form-control" 
                                id="inputEmail1"
                                placeholder="Email"
                                required="required" />
                        </div>
                    </div>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-key"></i></span>
                            <input 
                                type="password" 
                                name="password"
                                value={this.state.password} 
                                onChange={this.handleChange} 
                                class="form-control" 
                                id="inputPassword1"
                                placeholder="Password"
                                required="required" />
                        </div>
                    </div>        
                    <div class="form-group">
                        <button type="submit" class="btn btn-success btn-block login-btn">Sign in</button>
                    </div>
                    <div class="clearfix">
                        <label class="pull-left checkbox-inline"><input type="checkbox" /> Remember me</label>
                        <a href="/" class="pull-right text-success">Forgot Password?</a>
                    </div>  
                    
                </form>
                <div class="hint-text small">Don't have an account? <a href="/" class="text-success">Register Now!</a></div>
            </div>
        )
    }
}

export default Login;
