import { db } from './firebase';

// User API
export const doCreateUser = (userId, name, email, imageUrl) =>
    db.ref(`users/` + userId).set({
        username: name,
        email: email,
        profile_picture: imageUrl
    });

export const doGetUsers = () =>
    db.ref('/users/' + userId).once('value');
