import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

import { RegisterLink } from './Register';
import { PasswordForgetLink } from './PasswordForget';
import { auth } from '../../config';
import * as routes from '../constants/routes';

const LoginPage = ({ history }) =>
    <div>
        <h2 class="text-center">Sign in</h2>
        <LoginForm history={history} />
    </div>

const byPropKey = (propertyName, value) => () => ({
    [propertyName]: value,
});

const INITIAL_STATE = {
    email: '',
    password: '',
    error: null,
};

class LoginForm extends Component {
    constructor(props) {
        super(props);
        
        this.state = { ...INITIAL_STATE };
    }

    onSubmit = (e) => {
        const {
            email,
            password,
        } = this.state;

        const {
            history,
        } = this.props;

        auth.doSignInWithEmailAndPassword(email, password)
            .then(() => {
                this.setState({ ...INITIAL_STATE });
                history.push(routes.DASHBOARD);
            })
            .catch(error => {
                this.setState(byPropKey('error', error));
            });
        e.preventDefault();
    }

    render() {
        const {
            email,
            password,
            error,
        } = this.state;

        const isInvalid = 
            password === '' ||
            email === '';

        return (
            <div class="login-form">
                <form onSubmit={this.onSubmit}>
                    <div class="text-center social-btn">
                        <a href="/" class="btn btn-primary btn-block"><i class="fa fa-facebook"></i> Sign in with <b>Facebook</b></a>
                        <a href="/" class="btn btn-info btn-block"><i class="fa fa-twitter"></i> Sign in with <b>Twitter</b></a>
                        <a href="/" class="btn btn-danger btn-block"><i class="fa fa-google"></i> Sign in with <b>Google</b></a>
                    </div>
                    <div class="or-seperator"><i>or</i></div>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-envelope"></i></span>
                            <input 
                                type="email"
                                name="email" 
                                value={email} 
                                onChange={event => this.setState(byPropKey('email', event.target.value))} 
                                class="form-control" 
                                id="inputEmail1"
                                placeholder="Email"
                                required="required" />
                        </div>
                    </div>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-key"></i></span>
                            <input 
                                type="password" 
                                name="password"
                                value={password} 
                                onChange={event => this.setState(byPropKey('password', event.target.value))} 
                                class="form-control" 
                                id="inputPassword1"
                                placeholder="Password"
                                required="required" />
                        </div>
                    </div>        
                    <div class="form-group">
                        <button disabled={isInvalid} type="submit" class="btn btn-success btn-block login-btn">Sign in</button>
                    </div>
                    <div class="clearfix">
                        <label class="pull-left checkbox-inline"><input type="checkbox" /> Remember me</label>
                        <PasswordForgetLink class="pull-right text-success" />
                    </div>  
                    
                    { error && <p>{error.message}</p> }
                </form>                
                <div class="hint-text small"><RegisterLink class="text-success" /></div>
            </div>
        )
    }
}

export default withRouter(LoginPage);

export {
    LoginForm,
};
