import React, { Component } from 'react';
import { Link, withRouter, } from 'react-router-dom';

import { auth, db } from '../../config/';
import * as routes from '../constants/routes';

const Register = ({ history }) =>
    <div>
        <h2 class="text-center">Sign up</h2>
        <RegisterForm history={history} />
    </div>

const INITIAL_STATE = {
    username: '',
    email: '',
    passwordOne: '',
    passwordTwo: '',
    error: null,
};

const byPropKey = (propertyName, value) => () => ({
    [propertyName]: value,
});

class RegisterForm extends Component {
    constructor(props) {
        super(props);

        this.state = { ...INITIAL_STATE };
    }

    onSubmit = (e) => {
        const {
            username,
            email,
            passwordOne,
        } = this.state;
        
        const {
            history,
        } = this.props;

        auth.doCreateUserWithEmaiAndPassword(email, passwordOne)
            .then(authUser => {
                db.doCreateUser(authUser.user.uid, username, email)
                    .then(() => {
                        this.setState({ ...INITIAL_STATE });
                        history.push(routes.DASHBOARD);        
                    })
                    .catch(error => {
                        this.setState(byPropKey('error', error));
                    });
            })
            .catch(error => {
                this.setState(byPropKey('error', error));
            });
        e.preventDefault();
    }

    render() {
        const {
            username,
            email,
            passwordOne,
            passwordTwo,
            error,
        } = this.state;

        const isInvalid = 
            passwordOne !== passwordTwo || 
            passwordOne === '' ||
            email === '' ||
            username === '';

        return (
            <div class="login-form">
                <form onSubmit={this.onSubmit}>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-user"></i></span>
                            <input
                                type="text"
                                name="text" 
                                value={username} 
                                onChange={event => this.setState(byPropKey('username', event.target.value))}
                                class="form-control" 
                                id="inputUsername1"
                                placeholder="Full Name"
                                required="required"
                            />
                        </div>
                    </div>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-envelope"></i></span>
                            <input
                                type="email"
                                name="email" 
                                value={email} 
                                onChange={event => this.setState(byPropKey('email', event.target.value))} 
                                class="form-control" 
                                id="inputEmail1"
                                placeholder="Email"
                                required="required"
                            />
                        </div>
                    </div>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-key"></i></span>
                            <input
                                type="password" 
                                name="password"
                                value={passwordOne}
                                onChange={event => this.setState(byPropKey('passwordOne', event.target.value))}
                                class="form-control" 
                                id="inputPassword1"
                                placeholder="Password"
                                required="required"
                            />
                        </div>
                    </div>
                    <div class="form-group">
                        <div class="input-group">
                            <span class="input-group-addon"><i class="fa fa-key"></i></span>
                            <input
                                type="password" 
                                name="password"
                                value={passwordTwo}
                                onChange={event => this.setState(byPropKey('passwordTwo', event.target.value))}
                                class="form-control" 
                                id="inputPassword2"
                                placeholder="Confirm Password"
                                required="required"
                            />
                        </div>
                    </div>                    
                    <button disabled={isInvalid} type="submit" class="btn btn-success btn-block login-btn">Register</button>

                    { error && <p>{error.message}</p> }
                </form>
            </div>            
        );
    }
}

const RegisterLink = () =>
    <div class="hint-text small">
        Don't have an account?
        {' '}
        <Link to={routes.REGISTER} class="text-success">Register Now!</Link>
    </div>

export default withRouter(Register);

export {
    RegisterForm,
    RegisterLink,
};
