import React, { Component } from 'react';

import AddNewMahi from '../components/AddNewMahi';

const byPropKey = (propertyName, value) => () => ({
    [propertyName]: value,
});

const INITIAL_STATE = {
    user: 
    [{ 
        username: '', 
        email: '',
    }],
    organization: 
    [{ 
        farm_company: '',
        farm_name: '',
        farm_size: '',
        farm_address: '' 
    }],
    lot: 
    [{ 
        lot_designator: '',
        lot_size: '',
        lot_shape: '' 
    }],
    pesticide: '',
    error: null,
};

class Dashboard extends Component {
    constructor(props) {
        super(props);

        this.state = { ...INITIAL_STATE }
    }

    handleChange = (e) => {
        if (["username", "email"].includes(e.target.className) ) {
            const user = [...this.state.user]
            user[e.target.dataset.id][e.target.className] = e.target.value.toUpperCase();
            this.setState({ user }, () => console.log(this.state.user));
        } else {
            this.setState({ [e.target.name]: e.target.value.toUpperCase() });
        }
    }

    addMahi = (e) => {
        this.setState((prevState) => ({
            user: [...prevState.user, { 
                username: '', 
                email: '' 
            }],
            organization: [...prevState.organization, {
                farm_company: '',
                farm_name: '',
                farm_size: '',
                farm_address: ''
            }],
            lot: [...prevState.lot, {
                lot_designator: '',
                lot_size: '',
                lot_shape: ''
            }],
            pesticide: [...prevState.pesticide],
        }));
    }

    handleSubmit = (e) => { e.preventDefault(); };

    render() {
        const {
                user, 
                organization, 
                lot,
                pesticide,
                error,
            } = this.state;

        return (
            <form onSubmit={this.handleSubmit} onChange={this.handleChange} >
                <label htmlFor="username">Username</label> 
                <input 
                    type="text" 
                    name="username" 
                    id="username" 
                    value={username} 
                />
                <label htmlFor="pesticide">Pesticide</label> 
                <input 
                    type="text" 
                    name="pesticide" 
                    id="pesticide" 
                    value={pesticide} 
                />
                
                <button onClick={this.addMahi}>Add New Mahi</button>                
                <AddNewMahi user={user} />
                <input type="submit" value="Submit" /> 
            </form>
        );
    }    
}

export default Dashboard;
