import React, { Component } from 'react';

const byPropKey = (propertyName, value) => () => ({
    [propertyName]: value,
});

const INITIAL_STATE = {
    user: 
    [{ 
        username: '', 
        email: '',
    }],
    organization: 
    [{ 
        farm_company: '',
        farm_name: '',
        farm_size: '',
        farm_address: '' 
    }],
    lot: 
    [{ 
        lot_designator: '',
        lot_size: '',
        lot_shape: '' 
    }],
    pesticide: '',
    error: null,
};

class Dashboard extends Component {
    constructor(props) {
        super(props);

        this.state = { ...INITIAL_STATE };
    }

    handleChange = (e) => {
        if (["username", "email"].includes(e.target.className) ) {
            const user = [...this.state.user]
            user[e.target.dataset.id][e.target.className] = e.target.value.toUpperCase();
            this.setState({ user }, () => console.log(this.state.user));
        } else {
            this.setState({ [e.target.name]: e.target.value.toUpperCase() });
        }
    };

    addMahi = (e) => {
        this.setState((prevState) => ({
        user: [...prevState.user, { username: '', email: '' }],
        }));
    };

    handleSubmit = (e) => { e.preventDefault(); };

    render() {
        return (
            <div className="login-form">
                <form>
                    <label htmlFor="owner">Owner</label> 
                    <input type="text" name="owner" id="owner" />
                    <label htmlFor="description">Description</label> 
                    <input type="text" name="description" id="description" />
                    <button>Add New Mahi</button>
                    <input type="submit" value="Submit" /> 
                </form>
            </div>
        );
    }    
}

export default Dashboard;
